def contain_digit(str)
    if str =~ /[0-9]/
        return true
    else
        return false
    end
end

handle_number_digit = 0

myJournalists.each do |myJournalist|
    if contain_digit(myJournalist)
        handle_number_digit += 1
    end
end
puts "Combien d'handle contiennent un numéro ?"
puts "> #{handle_number_digit}"
puts ""

# Question 3 

def contain_aude(str)
    if str =~ /aude/i # le i = ignorer le sensi des strings ( maj ou minus )
        return true
    else
        return false
    end
end

handle_number_aude = 0

myJournalists.each do |myJournalist|
    if contain_aude(myJournalist)
        handle_number_digit += 1
    end
end

puts "Combien d'handle contiennent les 4 lettres du prénom \"Aude\" à la suite (sans prendre en compte les majuscules) ?"
puts "> #{handle_number_digit}"
puts ""

